mapper.update:
UPDATE declaration
   SET process = #{process}
 WHERE d_idx = #{d_idx}

choose.when:
update board
set   title   = #{title},
cont    = #{cont},
a_cont = #{a_cont}
where b_idx   = #{b_idx}

choose.otherwise:
update board
set   title   = #{title},
cont    = #{cont},
r_score = #{r_score}
where b_idx   = #{b_idx}

choose.when:
SELECT COUNT(*)
FROM BOARD
where menu_id = ${menu_id}

SELECT COUNT(*)
FROM BOARD
where menu_id = ${menu_id} and u_id = #{u_id}

choose.otherwise:
SELECT COUNT(*)
FROM BOARD
where menu_id = ${menu_id} and ${searchType} like '%${keyword}%' and u_id = #{u_id}

choose.when:
SELECT COUNT(*)
FROM BOARD
where menu_id = ${menu_id}

SELECT COUNT(*)
FROM BOARD
where menu_id = ${menu_id} and g_idx = ${g_idx}

mapper.update:
update board
set  b_count = b_count + 1
where b_idx = #{b_idx}

mapper.select:
SELECT COUNT(*)
  FROM board
 WHERE    u_id = #{u_id}
   AND   g_idx = ${g_idx}
   AND menu_id = 1
