mapper.select:
SELECT COUNT(*)
FROM REPLY
WHERE BOARD_NUMBER = #{board_number}

SELECT COUNT(*)
FROM RIDER_REPLY
WHERE BOARD_NUMBER = #{board_number}

mapper.update:
UPDATE REPLY
SET REND_PAGE = #{endPage}
WHERE BOARD_NUMBER = #{board_number}

UPDATE RIDER_REPLY
SET REND_PAGE = #{endPage}
WHERE BOARD_NUMBER = #{board_number}

UPDATE REPLY
SET REND_PAGE = #{endPage}
WHERE BOARD_NUMBER = #{board_number}

UPDATE REPLY
SET CONT = #{cont}
WHERE REPLY_NUMBER = #{reply_number}

UPDATE RIDER_REPLY
SET CONT= #{cont}
WHERE REPLY_NUMBER = #{reply_number}
