mapper.select:
SELECT RIDER_REPLY_NUMBER
FROM RIDER_REPLY_FILE
WHERE RIDER_REPLY_NUMBER = #{rider_reply_number}

        SELECT SFILENAME
        FROM RIDER_REPLY_FILE
        WHERE RIDER_REPLY_NUMBER = #{rider_reply_number}

        SELECT SFILENAME
        FROM CUSTOMER_BOARD_FILE
        WHERE CUSTOMER_BOARD_NUMBER = #{board_number}

mapper.update:
        UPDATE RIDER_REPLY_FILE
        SET
        FILENAME=#{vo.fileName},
        FILEEXIT=#{vo.fileExit},
        SFILENAME=#{vo.sFileName}

        WHERE RIDER_REPLY_NUMBER = #{rider_reply_number}
